import React from "react";
import "../styles/styles.scss";
import { Col } from "react-grid-system";

export default class WeatherInfo extends React.Component {
  getWeatherIcon(description, time) {
    let icon = "";
    if (description.includes("clear sky") && time >= 6 && time <= 18) {
      icon = "day";
    } else if (description.includes("clouds") && time >= 6 && time <= 18) {
      icon = "cloudyday";
    } else if (
      description.includes("clear sky") &&
      ((time >= 0 && time <= 6) || time >= 21)
    ) {
      icon = "moon";
    } else if (
      description.includes("clouds") &&
      ((time = 0 && time <= 6) || time >= 21)
    ) {
      icon = "cloudymoon";
    } else if (description.includes("rain")) {
      icon = "rain";
    }
    return icon;
  }

  render() {
    let icon = this.getWeatherIcon(this.props.description, this.props.time);
    return (
      <Col lg={2} className="has-text-centered is-family-sans-serif">
        <div className="is-size-5 has-text-weight-bold">{this.props.date}</div>
        <div>
          <span>{Math.round(this.props.minTemp)}&deg;</span> /{" "}
          <span>{Math.round(this.props.maxTemp)}&deg;C</span>
        </div>
        <div>Humidity: {this.props.humidity}</div>
        <div>Weather: {this.props.description}</div>
      </Col>
    );
  }
}
