import React from 'react';
import {Col} from 'react-grid-system';

export default class WeatherReport extends React.Component {
    getIcon(description, time) {
        let icon = '';
        if (description.includes('clear sky') && (time>=6 && time<=18)) icon='day';
        else if (description.includes('clouds') && (time>=6 && time<=18)) icon='cloudy';
        else if (description.includes('clear sky') && ((time>=0 && time<=6) || (time>=21))) icon='moon';
        else if (description.includes('clouds') && ((time>=0 && time<=6) || (time>=21))) icon='cloudymoon';
        else if (description.includes('rain')) icon='rain';
        return icon;
    }

    render() {
        let icon = this.getIcon(this.props.description, this.props.time);
        return (
            <Col lg={2}>
                <div>{this.props.city}</div>
                <div>{this.props.date}</div>
                <div><span className={icon}></span></div>
                <div>
                    <span>{this.props.maxTemp}&deg; / </span>
                    <span>{this.props.minTemp}&deg;</span>
                </div>
                <div>Humidity: {this.props.humidity}</div>
                <div>Weather: {this.props.description}</div>
            </Col>
        )
    }
}
