import React from 'react';

export default class WeatherReport extends React.Component {
  render() {
    const title = this.props.city ? null : <h1>Weather Forecast</h1>;
    return (
      <div>
        <div>
          {title}
          <div>
            <h3>Today</h3>
            <h1>{this.props.city}</h1>
            <p>Temperature: {this.props.data ? Math.round(this.props.data.temp) : 0}&deg;C</p>
            <p>{this.props.data ? this.props.data.weather.toLowerCase() : ""}</p>
          </div>
        </div>
        {this.props.children}
      </div>
    );
  }
}
