import React from "react";
import "../styles/styles.scss";

export default class SearchCity extends React.Component {
  render() {
    const clickHandler = async e => {
      e.persist();
      const eventKey = e.which ? e.which : e.keyCode;
      const city = e.target.value;

      if (eventKey === 13) {
        if (/^[a-zA-ZäöüÄÖÜß ]+$/.test(city)) {
          e.target.classList.add("Loading");
          if (await this.props.apiRequest(city)) {
            e.target.placeholder = "Enter City";
          } else {
            e.target.placeholder = "City Not Found. Try again.";
          }
        } else {
          e.target.placeholder = "Enter valid city.";
          e.target.classList.remove("Loading");
          e.target.value = "";
        }
      }
    };

    const style = {
      top: this.props.city ? '-380px' : '-20px',
      width: '600px',
      display: 'inline-block',
      padding: '10px 0px 10px 30px',
      lineHeight: '120%',
      position: 'relative',
      borderRadius: '20px',
      outline: 'none',
      fontSize: '20px',
      transition: 'all 0.5s ease-out'
    };

    return (
      <input type="text" placeholder="Enter City" onKeyPress={clickHandler} style={style} />
    );
  }
}
