import React from "react";

export default class WeatherInfo extends React.Component {
    getWeatherIcon(description, time) {
        let icon = "";
        if(description.includes('clear sky') && (time>=6 && time<=18)) {
			icon = 'day'
		} else if(description.includes('clouds') && (time>=6 && time<=18)){
			icon = 'cloudyday'
		} else if(description.includes('clear sky') && ((time>=0 && time<=6) || (time>=21))) {
			icon = 'moon'
		} else if(description.includes('clouds') && ((time=0 && time<=6) || (time>=21))) {
			icon = 'cloudymoon'
		} else if(description.includes('rain')) {
			icon = 'rain'
        }
        return icon;
    }

    render() {
        let icon = this.getWeatherIcon(description, time);
        return (
            <div className="columns">
            <div className="column">
                <div className="module-card-wrap">
                    <div className="module-card card">
                        <div className="card">
                            <div>{this.props.date}</div>
                            <div>
                                <span>
                                    <img src={`../images/${icon}.png`} />
                                </span>
                            </div>
                            <div>
                                <span>{Math.round(this.props.lowTemp)}&deg;C</span> /
                                <span>{Math.round(this.props.highTemp)}&deg;C</span>
                            </div>
                            <div>Humidity: {this.props.humidity}</div>
                            <div>Weather: {this.props.weather}</div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
        )
    }
}
