import React from "react";
import axios from "axios";
import "../styles/styles.scss";
import WeatherReport from "../components/WeatherReport";
import SearchCity from "../components/SearchCity";
import WeatherCard from "../components/WeatherCard";

export default class Index extends React.Component {
  constructor(props) {
    super(props);
    this.apiUrl =
      "http://api.openweathermap.org/data/2.5/forecast?units=metric&";
    this.apiKey = "b71deb2566d82e77a5e670d0d3771d2a";
    this.state = {
      city: undefined,
      days: new Array(5)
    };
  }

  updateState(data) {
    const city = data.city.name;
    const days = [];
    const dayIndex = this.getDayIndex(data);
    for (let i = 0; i < 6; i++) {
      days.push({
        date: data.list[dayIndex[i]].dt_txt,
        weather: data.list[dayIndex[i]].weather[0].description,
        icon: data.list[dayIndex[i]].weather[0].icon,
        temperature: data.list[dayIndex[i]].main.temp
      });
    }
    this.setState({
      city: city,
      days: days
    });
  }

  componentDidMount() {
    this.apiRequest(this.state.activeCity);
  }

  async apiRequest(cityName) {
    const weatherUrl = `${this.apiUrl}q=${cityName}&appid=${this.apiKey}`;
    await axios
      .get(weatherUrl)
      .then(res => {
        res.json();
      })
      .catch(err => console.log(err));
  }

  getDayIndex(data) {
    let dayIndex = [];
    dayIndex.push(0);
    let index = 0;
    let time = data.list[index].dt_txt.slice(8, 10);
    for (let i = 0; i < 5; i++) {
      while (time || data.list[index].dt_txt.slice(11, 13) !== "15") {
        index++;
      }
      dayIndex.push(index);
      time = data.list[index].dt_txt.slice(8, 10);
    }
    return dayIndex;
  }

  render() {
    const WeatherCards = () => {
      const weatherCards = this.state.days.slice(1).map(day => (
        <li>
          <WeatherCard {...day} />
        </li>
      ));
      return <ul>{weatherCards}</ul>;
    };

    return (
      <div className="container">
        <header className="app">
          <WeatherReport city={this.state.city} data={this.state.days[0]}>
            <SearchCity
              city={this.state.city}
              apiRrequest={this.apiRequest.bind(this)}
            />
            <WeatherCards />
          </WeatherReport>
        </header>
      </div>
    );
  }
}
