import React from 'react';
import "../styles/styles.scss";

const UnitToggle = ({ toggleUnit }) => (
    <div className="field" onClick={toggleUnit}>
        <input className="is-checkradio" type="radio" id="celsius" name="celsius" value="metric" />
        <label for="celsius">Celsius</label>
        <input className="is-checkradio" type="radio" id="fahrenehit" name="fahrenheit" value="fahrenheit" />
        <label for="fahrenehit">Fahreneheit</label>
    </div>
);

export default UnitToggle;
