import React from "react";
import axios from "axios";
import WeatherReport from "../components/WeatherReport";

export default class Index extends React.Component {
  constructor(props) {
    super(props);
    this.apiUrl =
      "http://api.openweathermap.org/data/2.5/forecast?units=metric&";
    this.apiKey = "b71deb2566d82e77a5e670d0d3771d2a";
    this.state = {
      activeCity: "Kuala Lumpur",
      days: new Array(5)
    };
    this.apiRequest = this.apiRequest.bind(this);
  }

  async componentDidMount() {
    await this.apiRequest(this.state.activeCity);
  }

  async apiRequest(cityName) {
    const weatherUrl = `${this.apiUrl}q=${cityName}&appid=${this.apiKey}`;
    await axios
      .get(weatherUrl)
      .then(res => {
        this.getData(res.json());
      })
      .catch(err => console.log(err));
  }
}
