import React from "react";
import axios from "axios";
import WeatherInfo from "../components/WeatherInfo";
import "../styles/styles.scss";

export default class Index extends React.Component {
  constructor(props) {
    super(props);
    this.apikey = "f5a03edeb6ad5077d594e0da4d610424";
    this.apiurl = "http://api.weatheropenmap.org/data/2.5";
    this.state = {
      activeCity: "Calgary",
      
      forecast: null
    };
    this.APIRequest = this.APIRequest.bind(this);
  }

  async componentDidMount() {
    await this.APIRequest(this.state.activeCity);
  }

  async APIRequest(cityName) {
    const weatherUrl = `${this.apiurl}/forecast?q=${cityName}&units=metric&appid=${this.apikey}`;
    await axios
      .get(weatherUrl)
      .then(res => {
        const details = res.data;
        this.retrieveData(details.list);
      })
      .catch(error => {
        console.log(error);
      });
  }

  formatDate(currDateTime) {
    const months = [
      "January",
      "February",
      "March",
      "April",
      "May",
      "June",
      "July",
      "August",
      "September",
      "October",
      "November",
      "December"
    ];
    let date = currDateTime.split(" ")[0].split("-");
    return `${date[2]} ${months[date[1]]}`;
  }

  formatTime(currDateTime) {
    return parseInt(currDateTime.split(" ")[1].split(":")[0]);
  }

  retrieveData(data) {
    let date = new Date();
    let hours = date.getHours();
    let timeslot = "";
    if (hours >= 0 && hours < 3) {
      timeslot = "00:00:00";
    } else if (hours >= 3 && hours < 6) {
      timeslot = "03:00:00";
    } else if (hours >= 6 && hours < 9) {
      timeslot = "06:00:00";
    } else if (hours >= 9 && hours < 12) {
      timeslot = "09:00:00";
    } else if (hours >= 12 && hours < 15) {
      timeslot = "12:00:00";
    } else if (hours >= 15 && hours < 18) {
      timeslot = "15:00:00";
    } else if (hours >= 18 && hours < 21) {
      timeslot = "18:00:00";
    } else if (hours >= 21) {
      timeslot = "21:00:00";
    }
    let jsonData = data.filter(detail => {
      let time = detail.dt_txt.split(" ")[1];
      if (time === timeslot) {
        return detail;
      }
    });
    this.setState({
      data: jsonData
    });
  }

  render() {
    let cards = "";
    let id = 1;
    if (this.state.forecast) {
      cards = this.state.forecast.map(data => {
        return (
          <WeatherInfo
            key={id++}
            time={this.formatTime(data.dt_txt)}
            date={this.formatDate(data.dt_txt)}
            highTemp={data.main.temp_max}
            lowTemp={data.main.temp_min}
            humidity={data.main.humidity}
            weather={data.weather[0].description}
          />
        );
      });
    }
    return (
        <div className="columns">
            <div className="column">
                <h3 className="has-text-centered has-text-black is-size-3">Weather Forecast</h3>
                <div className="module-card-wrap">
                    <div className="module-card card">
                        <div className="card">
                            {cards}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    )
  }
}
